templates.template:
    /**
	 * Registers ${x:argType} in IOC container.		
	 */
	public ObjectProvider build${x:argType}Provider()
	{
		return new ObjectProvider()
		{
			/**
			 * @see org.apache.tapestry.ioc.ObjectProvider#provide
			 */
			public <T> T provide(Class<T> objectType, AnnotationProvider annotationProvider, ObjectLocator locator)
			{
				if (objectType == ${x:argType}.class)
				{
                    //TODO ${user}
					${x:argType} instance = null;					
					return objectType.cast(instance);
				}				
				return null;
			}
		};
	}
	
	/**
	 * Contributes ${x:argType} provider defined in method {@link #build${x:argType}Provider()}
	 * to global object provider registry. 
	 */
	public void contributeMasterObjectProvider(
			@InjectService("${x:argType}Provider") ObjectProvider provider, 
			OrderedConfiguration<ObjectProvider> configuration)
	{
		configuration.add("${x:argType}", provider);
	}
